mapper.select:
SELECT post_id, user_id, style_category, title, content, thumbnail_image_url,
like_count, comment_count, view_count, save_count, created_at, updated_at
FROM POST
WHERE user_id = #{userId}
AND post_id != #{excludePostId}
LIMIT 4

mapper.update:
UPDATE POST
SET title = #{postDTO.title},
content = #{postDTO.content},
style_category = #{postDTO.styleCategory},
thumbnail_image_url = #{postDTO.thumbnailImageUrl}
WHERE post_id = #{postDTO.postId}

mapper.select:
SELECT view_count
FROM POST
WHERE post_id = #{postId}

SELECT COUNT(*)
FROM POST_LIKE
WHERE post_id = #{postId}

SELECT COUNT(*) > 0
FROM POST_LIKE
WHERE post_id = #{postId} AND user_id = #{userId}

SELECT like_count
FROM POST
WHERE post_id = #{postId}

mapper.update:
UPDATE POST
SET like_count = #{likeCount}
WHERE post_id = #{postId}

mapper.select:
SELECT COUNT(*)
FROM POST_SAVE
WHERE post_id = #{postId}

SELECT COUNT(*) > 0
FROM POST_SAVE
WHERE post_id = #{postId} AND user_id = #{userId}

SELECT save_count
FROM POST
WHERE post_id = #{postId}

mapper.update:
UPDATE POST
SET save_count = #{saveCount}
WHERE post_id = #{postId}

mapper.select:
SELECT COUNT(*) > 0
FROM FOLLOW
WHERE follower_id = #{followerId} AND following_id = #{followingId}
